// Normalize data from all trigger types into a standard format
const items = $input.all();

if (!items.length) {
  throw new Error('No input data received');
}

function resolveTriggerName(item, data) {
  const paired = item.pairedItem;
  const source = Array.isArray(paired) ? paired[0]?.source : paired?.source;
  if (source?.node) {
    return source.node;
  }

  if (data.subject || data.bodyPreview) {
    return 'Outlook Email Trigger';
  }

  if (data.body?.content && data.from?.user) {
    return 'Teams Message Trigger';
  }

  return 'Webhook Create Task';
}

return items.map((item) => {
  const data = item.json ?? {};
  const triggerName = resolveTriggerName(item, data);
  let normalized;

  switch (triggerName) {
    case 'Outlook Email Trigger':
      normalized = {
        title: data.subject || 'Untitled Email',
        content: data.bodyPreview || data.body?.content || '',
        sender: data.from?.emailAddress?.address || 'Unknown',
        sender_name: data.from?.emailAddress?.name || 'Unknown',
        source: 'outlook',
        source_ref: data.id,
        raw_data: JSON.stringify(data),
      };
      break;

    case 'Teams Message Trigger':
      {
        const preview = data.body?.content || '';
        normalized = {
          title: 'Teams: ' + (preview ? preview.substring(0, 50) : 'Message') + '...',
          content: preview,
          sender: data.from?.user?.mail || data.from?.user?.userPrincipalName || 'Unknown',
          sender_name: data.from?.user?.displayName || 'Unknown',
          source: 'teams',
          source_ref: data.id,
          raw_data: JSON.stringify(data),
          channel: data.channelIdentity?.displayName,
        };
      }
      break;

    case 'Webhook Create Task':
    default:
      normalized = {
        title: data.title || 'Manual Task',
        content: data.description || '',
        sender: data.requester || 'API',
        sender_name: data.requester || 'API User',
        source: 'manual',
        source_ref: null,
        raw_data: JSON.stringify(data),
        est_minutes: data.est_minutes,
        due_text: data.due_text,
        role_hint: data.role_hint,
      };
      break;
  }

  return { json: normalized };
});

const items = $input.all();

return items.map((item, index) => {
  const data = item.json || {};
  const timestamp = Date.now();
  const randomSuffix = Math.floor(Math.random() * 1000);
  const id = (data.source || 'task') + '_' + timestamp + '_' + randomSuffix;
  const roleHint = (data.role_hint || '').toLowerCase();
  const requesterRole = roleHint.includes('leder') || roleHint.includes('manager') ? 'MANAGER' : 'EMPLOYEE';

  const payload = {
    id: id,
    title: data.title || 'Untitled Task',
    description: data.content || '',
    category: 'SUPPORT',
    requester_role: requesterRole,
    requester_name: data.sender_name || 'Unknown',
    created_at: new Date().toISOString(),
    business_value: null,
    risk_level: null,
    estimated_effort_hours: data.est_minutes ? Number(data.est_minutes) / 60 : null,
    workaround_available: null,
    affected_users_count: null,
    context: data.content || '',
    tags: data.channel ? [data.channel] : [],
  };

  return {
    json: payload,
    pairedItem: [{ item: index }],
  };
});

